    fun getAll(): List<String> {
        val universityNames = mutableListOf<String>()

        val document: Document = Jsoup.parse(unis)
        val elements = document.select(".item.selected, .item")

        for (element in elements) {
            val universityName = element.text().substringAfter("«").replace("»", "")
            universityNames.add(universityName)
        }

        return universityNames
    }
